import { Injectable } from '@angular/core';
import { BehaviorSubject } from 'rxjs';
import { HttpClient } from '@angular/common/http';
import { Router } from '@angular/router';
import { IUser } from '../interfaces/user.model';
import { IResponse } from '../interfaces/response.model';
import { handleRejectionMessage } from '../utils/handle-rejections';

@Injectable({
  providedIn: 'root',
})
export class AuthService {
  public userSubject$: BehaviorSubject<IUser | null>;

  constructor(private http: HttpClient, private router: Router) {
    const token = JSON.parse(<string>localStorage.getItem('USER_DATA'));
    const user = JSON.parse(<string>localStorage.getItem('PROFILE'));
    this.userSubject$ = new BehaviorSubject<IUser | null>({ ...user, token });
    this.userSubject$.next(user);
  }

  async initUser(token: { token: string }): Promise<void> {
    localStorage.setItem('USER_DATA', JSON.stringify(token));
    this.getProfile().then((res) => {
      this.userSubject$.next({ ...token, ...res.data });
      // this.highlightTextService.init();
    });
  }

  public get currentUserValue(): IUser | null {
    return this.userSubject$.value;
  }

  async login(username: string, password: string): Promise<any> {
    try {
      const loginResult = await this.http
        .post<any>(`/api/auth/login`, {
          username,
          password,
        })
        .toPromise();

      if (loginResult.result === 'success') {
        if (loginResult.data) {
          const token: { token: string } = { ...loginResult.data };
          this.initUser(token);
          return {
            result: 'success',
            message: 'Login exitoso',
          };
        } else this.userSubject$.next(null);
      } else if (loginResult.result === 'error') {
        return {
          result: 'error',
          message: loginResult.message,
          data: loginResult.data,
        };
      }
    } catch (error) {
      return handleRejectionMessage(error);
    }
  }

  async resetPassword(email: string): Promise<IResponse> {
    const response = await this.http
      .post<any>(`/api/auth/forgot`, { email })
      .toPromise();

    return {
      result: response.result,
      message: response.message,
    };
  }

  async changeImage(image: string): Promise<IResponse> {
    try {
      const response = await this.http
        .post<IResponse>(`/api/user/change-image`, {
          image,
        })
        .toPromise();
      if (response.result === 'success') {
        const currentUserValue = this.currentUserValue;
        const updatedUser = { ...currentUserValue, img: image };
        this.userSubject$.next(<IUser>updatedUser);
        delete updatedUser.token;
        localStorage.setItem('PROFILE', JSON.stringify(updatedUser));

        return {
          result: 'success',
          message: response.message,
        };
      } else {
        return {
          result: 'error',
          message: response.message,
        };
      }
    } catch (e) {
      return {
        result: 'error',
        message: e.message,
      };
    }
  }

  async changePassword(
    oldPassword: string,
    newPassword: string,
  ): Promise<IResponse> {
    try {
      const response = await this.http
        .post<IResponse>(`/api/user/change-password`, {
          oldPassword,
          newPassword,
        })
        .toPromise();
      if (response.result === 'success') {
        return {
          result: 'success',
          message: response.message,
        };
      } else {
        return {
          result: 'error',
          message: response.message,
        };
      }
    } catch (e) {
      return {
        result: 'error',
        message: e.message,
      };
    }
  }

  async getProfile(): Promise<IResponse> {
    const response = await this.http
      .get<IResponse>(`/api/user/profile`)
      .toPromise();
    if (response.result === 'success') {
      if (!response.data.img || response.data.img === '') {
        response.data.img = `data:image/png;base64,`;
      }
      localStorage.setItem('PROFILE', JSON.stringify(response.data));
      return {
        result: 'success',
        message: response.message,
        data: response.data,
      };
    } else {
      return {
        result: 'error',
        message: response.message,
      };
    }
  }

  logout(): void {
    // remove user from local storage to log user out
    localStorage.removeItem('USER_DATA');
    localStorage.removeItem('PROFILE');
    this.userSubject$.next(null);
    this.router.navigate(['login']);
  }
}
